mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, photoes = #{photoes}
WHERE id = #{id};

mapper.select:
select max(id)
from board
where category=1;

select max(id)
from board
where category=3;

SELECT COUNT(*) > 0
FROM board_likes
WHERE post_id = #{id} AND user_id = #{userEmail}

mapper.update:
UPDATE board
SET `like` = (SELECT COALESCE(COUNT(*), 0) FROM board_likes WHERE post_id = #{id})
WHERE id = #{id}

UPDATE board
SET views = board.views + 1
WHERE id = #{id};

mapper.select:
SELECT views
FROM board
WHERE id = #{id};
